type IconName = keyof typeof iconLibrary;

type IconProps = {
  name: IconName;
  width?: number | string;
  height?: number | string;
  className?: string;
};

export default function Icon({
  name,
  width,
  height = width,
  className = "",
}: IconProps) {
  const iconPath = iconLibrary[name];

  if (!iconPath) {
    console.warn(`Icon "${name}" not found`);
    return null;
  }

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      width={width}
      height={height}
      viewBox="0 0 24 24"
      className={className}
    >
      {iconPath}
    </svg>
  );
}
